ruleBookInfo.tocUrl:
@js: 
so = java.ajax(baseUrl.replace(/book/,"chapter"));
java.setContent(so);
var id = baseUrl.match(/book\/(.*?)\.html/)[1];
java.put("id",id);
var pages = java.getStringList(".dropDown@li@data-p");
java.put("page",pages.length);
result = source.key + "/index.php?action=loadChapterPage" + "," + JSON.stringify({
  "body": `id=${id}&page=1`,
  "method": "POST"
});

ruleToc.chapterList:
$.data[*]
<js>
result=result
aa=/\{.*?"chapterorder":"(\d+)".*?\}/g
li=[]
let match
while ((match = aa.exec(result)) !== null) {
  li.push({
    id: parseInt(match[1], 10),
    content: match[0]
  });
}
li.sort((a, b) => a.id - b.id)
//java.log(JSON.stringify(li))
so=li.map(item => item.content).join(',')
s=so.replace(/^/,'{"list":[').replace(/$/,']}')
s
</js>$.list[*]

ruleToc.nextTocUrl:
@js: result = []
id = java.get("id");
pages = java.get("page");
java.log(pages)
for (let i = 2 ; i <= pages ; i++) {
option = source.key + "/index.php?action=loadChapterPage" + "," + JSON.stringify({
  "body": `id=${id}&page=${i}`,
  "method": "POST"
});
result.push(option);
}
result;

searchUrl:
@js: 
var url = java.ajax(source.key).match(/window\.location\.href=\'([^']+)/)[1];
cookie.removeCookie(source.key);
java.log(url);
cookie.removeCookie(url)
var kku = url.replace(/m\/.*html$/,"m");
java.log(kku);

cs = java.get(url,{})
cc = cs.headers().get("set-cookie")
cookie.setCookie(source.key, cc)

var ckk = String(cs.body());
//java.log(ckk)
if (ckk.match(/Checking your browser before accessing/)) {
   java.webView(null, url, "document.cookie")
   cok = cookie.getCookie(url)
   head = JSON.stringify({ headers: { 'Cookie': cok } })
   u = url + "," + head
   java.ajax(u)
}
var result = kku + "/k-{{java.base64Encode(java.encodeURI(key, 'utf-8'))}}-{{page}}.html"
result;

